    83	            left: -35px;
    84	            top: 20px;
    85	            width: 12px;
    86	            height: 12px;
    87	            border-radius: 50%;
    88	            background: #fff;
    89	            border: 2px solid #666;
    90	        }
    91	        .event.user::before {
    92	            border-color: #2196F3;
    93	            background: #2196F3;
    94	        }
    95	        .event.agent::before {
    96	            border-color: #4CAF50;
    97	            background: #4CAF50;
    98	        }
    99	        .event.environment::before {
   100	            border-color: #FF9800;
   101	            background: #FF9800;
   102	        }
   103	        .event-header {
   104	            display: flex;
   105	            justify-content: space-between;
   106	            margin-bottom: 10px;
   107	            font-size: 0.9em;
   108	            color: #666;
   109	        }
   110	        .event-content {
   111	            margin-top: 10px;
   112	        }
   113	        .event-content pre {
   114	            background: #f8f9fa;
   115	            padding: 10px;
   116	            border-radius: 4px;
   117	            overflow-x: auto;
   118	        }
   119	        .metadata {
   120	            margin-top: 10px;
   121	            padding: 10px;
   122	            background: #f8f9fa;
   123	            border-radius: 4px;
   124	            font-size: 0.9em;
   125	        }
   126	        .metadata-toggle {
   127	            color: #2196F3;
   128	            cursor: pointer;
   129	            user-select: none;
   130	        }
   131	        .hidden {
   132	            display: none;
   133	        }
   134	        .source-badge {
   135	            padding: 2px 8px;
   136	            border-radius: 12px;
   137	            font-size: 0.8em;
   138	            font-weight: 500;
   139	        }
   140	        .source-user {
   141	            background: #E3F2FD;
   142	            color: #1565C0;
   143	        }
   144	        .source-agent {
   145	            background: #E8F5E9;
   146	            color: #2E7D32;
   147	        }
   148	        .source-environment {
   149	            background: #FFF3E0;
   150	            color: #E65100;
   151	        }
   152	        #file-input {
   153	            display: none;
   154	        }
   155	        .upload-btn {
   156	            display: inline-block;
   157	            padding: 10px 20px;
   158	            background: #2196F3;
   159	            color: white;
   160	            border-radius: 4px;
   161	            cursor: pointer;
   162	            margin-bottom: 20px;
   163	            transition: background 0.3s ease;
   164	        }
   165	        .upload-btn:hover {
   166	            background: #1976D2;
   167	        }
   168	        .markdown {
   169	            margin-top: 10px;
   170	        }
   171	        .markdown h1 { font-size: 1.5em; margin-top: 1em; }
   172	        .markdown h2 { font-size: 1.3em; margin-top: 0.8em; }
   173	        .markdown h3 { font-size: 1.1em; margin-top: 0.6em; }
   174	        .markdown pre { background: #f8f9fa; padding: 10px; border-radius: 4px; overflow-x: auto; }
   175	        .markdown code { background: #f8f9fa; padding: 2px 4px; border-radius: 3px; }
