   178	<body>
   179	    <div class="filter-bar">
   180	        <div class="filter-group">
   181	            <label class="filter-label">
   182	                <input type="checkbox" class="filter-checkbox" data-source="user" checked>
   183	                <span class="source-badge source-user">User</span>
   184	            </label>
   185	            <label class="filter-label">
   186	                <input type="checkbox" class="filter-checkbox" data-source="agent" checked>
   187	                <span class="source-badge source-agent">Agent</span>
   188	            </label>
   189	            <label class="filter-label">
   190	                <input type="checkbox" class="filter-checkbox" data-source="environment" checked>
   191	                <span class="source-badge source-environment">Environment</span>
   192	            </label>
   193	        </div>
   194	        <label class="upload-btn" for="file-input">
   195	            Upload Conversation JSON
   196	        </label>
   197	        <input type="file" id="file-input" accept=".json">
   198	    </div>
   199	    <div class="container">
   200	        <div id="timeline" class="timeline"></div>
   201	    </div>
   202	
   203	    <script src="https://cdnjs.cloudflare.com/ajax/libs/marked/4.0.2/marked.min.js"></script>
   204	    <script>
   205	        function formatTimestamp(timestamp) {
   206	            const date = new Date(timestamp);
   207	            return date.toLocaleString();
   208	        }
   209	
   210	        function createSourceBadge(source) {
   211	            return `<span class="source-badge source-${source.toLowerCase()}">${source}</span>`;
   212	        }
   213	
   214	        function renderMarkdown(text) {
   215	            if (!text) return '';
   216	            try {
   217	                return marked.parse(text);
   218	            } catch (e) {
   219	                return text;
   220	            }
   221	        }
   222	
   223	        function createEventElement(event) {
   224	            const div = document.createElement('div');
   225	            div.className = `event ${event.source.toLowerCase()}`;
   226	            
   227	            let headerContent = `
   228	                <div class="event-header">
   229	                    <div>
   230	                        ${createSourceBadge(event.source)}
   231	                        <span style="margin-left: 10px;">ID: ${event.id}</span>
   232	                    </div>
   233	                    <div>${formatTimestamp(event.timestamp)}</div>
   234	                </div>
   235	            `;
   236	
   237	            let content = '';
   238	            
   239	            // Handle message content
   240	            if (event.message) {
   241	                content += `
   242	                    <div class="event-content">
   243	                        <div class="markdown">${renderMarkdown(event.message)}</div>
   244	                    </div>
   245	                `;
   246	            }
   247	
   248	            // Handle action and args
   249	            if (event.action) {
   250	                content += `
   251	                    <div class="event-content">
   252	                        <strong>Action:</strong> ${event.action}
   253	                    </div>
   254	                `;
   255	                if (event.args) {
   256	                    content += `
   257	                        <div class="event-content">
   258	                            <pre>${JSON.stringify(event.args, null, 2)}</pre>
   259	                        </div>
   260	                    `;
   261	                }
   262	            }
   263	
   264	            // Handle observation and content
   265	            if (event.observation) {
   266	                content += `
   267	                    <div class="event-content">
   268	                        <strong>Observation:</strong> ${event.observation}
   269	                    </div>
   270	                `;
   271	                if (event.content) {
   272	                    content += `
   273	                        <div class="event-content">
   274	                            <pre>${event.content}</pre>
   275	                        </div>
   276	                    `;
   277	                }
   278	            }
   279	
   280	            // Add metadata toggle and content
   281	            const metadata = {
   282	                ...event.tool_call_metadata,
   283	                ...event.extras
   284	            };
   285	            
   286	            if (Object.keys(metadata).length > 0) {
   287	                content += `
   288	                    <div class="metadata-toggle" onclick="toggleMetadata(${event.id})">
   289	                        Show Metadata ▼
   290	                    </div>
   291	                    <div id="metadata-${event.id}" class="metadata hidden">
   292	                        <pre>${JSON.stringify(metadata, null, 2)}</pre>
   293	                    </div>
   294	                `;
   295	            }
   296	
   297	            div.innerHTML = headerContent + content;
   298	            return div;
   299	        }
   300	
   301	        function toggleMetadata(eventId) {
   302	            const metadata = document.getElementById(`metadata-${eventId}`);
   303	            const toggle = metadata.previousElementSibling;
   304	            
   305	            if (metadata.classList.contains('hidden')) {
   306	                metadata.classList.remove('hidden');
   307	                toggle.textContent = 'Hide Metadata ▲';
   308	            } else {
   309	                metadata.classList.add('hidden');
   310	                toggle.textContent = 'Show Metadata ▼';
   311	            }
   312	        }
   313	
   314	        let currentConversation = [];
   315	
   316	        function getVisibleSources() {
   317	            const checkboxes = document.querySelectorAll('.filter-checkbox');
   318	            return Array.from(checkboxes)
   319	                .filter(cb => cb.checked)
   320	                .map(cb => cb.dataset.source);
   321	        }
   322	
   323	        function updateVisibility() {
   324	            const visibleSources = getVisibleSources();
   325	            const events = document.querySelectorAll('.event');
   326	            
   327	            events.forEach(event => {
   328	                const source = event.classList[1]; // user, agent, or environment
   329	                event.style.display = visibleSources.includes(source) ? 'block' : 'none';
   330	            });
   331	        }
   332	
   333	        function loadConversation(conversation) {
   334	            currentConversation = conversation;
   335	            const timeline = document.getElementById('timeline');
   336	            timeline.innerHTML = '';
   337	            
   338	            conversation.forEach(event => {
   339	                const eventElement = createEventElement(event);
   340	                timeline.appendChild(eventElement);
   341	            });
   342	            
   343	            updateVisibility();
   344	        }
   345	
   346	        // File input handler
   347	        document.getElementById('file-input').addEventListener('change', (e) => {
   348	            const file = e.target.files[0];
   349	            if (file) {
   350	                const reader = new FileReader();
   351	                reader.onload = (e) => {
   352	                    try {
   353	                        const conversation = JSON.parse(e.target.result);
   354	                        loadConversation(conversation);
   355	                    } catch (error) {
   356	                        alert('Error parsing JSON file: ' + error.message);
   357	                    }
   358	                };
   359	                reader.readAsText(file);
   360	            }
   361	        });
   362	
   363	        // Add event listeners for filter checkboxes
   364	        document.querySelectorAll('.filter-checkbox').forEach(checkbox => {
   365	            checkbox.addEventListener('change', updateVisibility);
   366	        });
   367	
   368	        // Load example.json by default
   369	        fetch('example.json')
   370	            .then(response => response.json())
   371	            .then(conversation => {
   372	                loadConversation(conversation);
   373	            })
   374	            .catch(error => {
   375	                console.error('Error loading example.json:', error);
   376	            });
   377	    </script>
   378	</body>
   379	</html>

